import React ,{useState} from 'react'

export default function TextForm(props) {

    const handleUpClick=()=>{
        // console.log("Uppercase was Clicked.."+text);
        let newText = text.toUpperCase();
        setText(newText);
        props.showAlert("Converted to Uppercase "," Success");
    }

    const handleLowClick = ()=>{
        let newText = text.toLowerCase();
        setText(newText);
        props.showAlert("Converted to Lowercase "," Success");

    }
    const handleClearClick= ()=>{
        let newText="";
        setText(newText);
        props.showAlert("Clear  "," Success");

    }
    const handleOnChange=(event)=>{
        // console.log("On Change..");
        setText(event.target.value);
    }
    

    const [text , setText]=useState("");
    // text ="new text" // Wrong Way... 
    // setText("Text here..."); // Correct way
    return (
    <>
        <div className="container" style={{color :props.mode==='dark'?'white':'#042743' }}>
            <h1>{props.heading}</h1>
            <div className=" mb-3">
                <textarea className="form-control" onChange={handleOnChange} style={{backgroundColor :props.mode==='dark'?'#042743':'white',color :props.mode==='dark'?'white':'#042743' }} value={text} id="myBox" rows="8" placeholder='Enter Here...'></textarea>
            </div>
            <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to Uppercase</button>
            <button className="btn btn-primary mx-2" onClick={handleLowClick}>Convert to Uppercase</button>
            <button className="btn btn-primary mx-2" onClick={handleClearClick}> To Clear text</button>
        </div>
        <div className="container my-3 " style={{color :props.mode==='dark'?'white':'#042743' }}>
            <h1>Your text Summary</h1>
            <p>{text.split(' ').length} words and {text.length} characters</p>
            <p>{0.008 * text.split(' ').length} Minutes to read</p>
            <h2>Preview</h2>
            <p>{text.length>0 ? text:"Enter something in the textbox to preview here..☝"}</p>
        </div>
    </>
  )
}
